INSERT INTO `product` VALUES (11,'169623fdc87cca849c216c7b4ebca4a5_d57f3c7b7be64d71917e6056a2fc012a_master (3).webp, 020120fd73ae622517a3e4f07fd61e1d_eab5331b1e8f4abda907ab228237c667_master (1).jpg, b288e3be3d80e82f391b29275b4c9cbf_41201056fe1148518d4840a2d0cc2726_master (1).jpg, 9ab83ee32f6f0f9c551096897e6327db_3aaaff455a8a4a40b1657aa2d2f7ce0b_master (1).jpg',NULL,4000,4000,'<h2 style=\"margin-left:0px;\"><strong>Kem Thưởng AATAS Nhiều vị Cho Mèo Làm Từ Nguyên Liệu Tươi Ngon, Đa Dạng Vị, Không Chứa Ngũ Cốc Và Các Chất Có Hại Cho Mèo</strong></h2><h3 style=\"margin-left:0px;\"><strong>- Đặc điểm sản phẩm:</strong></h3><ul><li><p style=\"margin-left:0px;\">Sốt kem Aatas Cream được làm từ thịt tươi, thơm ngon và giàu dinh dưỡng</p></li><li><p style=\"margin-left:0px;\">Mỗi loại sốt được làm từ thịt khác nhau, phục vụ các hoàng thượng có khẩu vị khác nhau, tạo nên bữa ăn hấp dẫn</p></li><li><p style=\"margin-left:0px;\">Đa dạng vị: cá ngừ, cá hồi, gà</p></li><li><p style=\"margin-left:0px;\">Công thức chế biến từ thịt cá tươi, với nước sốt Gravy ít béo và ít calo,bảo vệ sức khỏe cho các bé cưng.</p></li><li><p style=\"margin-left:0px;\">Aatas sử dụng nguồn thực phẩm tốt nhất, không độn ngũ cốc, không biến đổi gen, không chấy bảo quản và hóa chất độc hại</p></li></ul><h3 style=\"margin-left:0px;\"><strong>- Thông tin sản phẩm:</strong></h3><p style=\"margin-left:0px;\"><strong>Khối lượng sp:</strong> 1 gói 16gr</p>',0,100,'Kem Thưởng/ Súp Thưởng AATAS Cream 16g (Singapore)',_binary '',0,0,4,8,0),(12,'201edd8db5e4c15d6c3c100156ce5817_23644fcd3d3d4b49ac5d9f0e57c81199_master.jpg, df6f123dd6c920201159f3a1ef0cc66e_2b17543c10294247822e1a04b8e7f449_master.jpg, 89a799367800a2ca82de6a3e3657650b_887d7bae2f374ed9897d4af4a0e7c88e_master.jpg, 6a9166cef438ace8672187acf3a03e1f_cd9e52a18b6645ad93f16485b3813b60_master (1).webp, 64a414948de21cc092f96335896ef041_6b19df14831f40f69403cc665ac55606_master.jpg, 790f63123a746e24777ec7709b3cc3f3_5c67ca96c4a94dbba66d4b067dcbab2c_master.jpg, 103d1fb4cafc87e9a18dd6c65365eac0_dbfe44dd81f74e8e91828fe5572f802c_master.png, ca-ngu-plus-ca-bao-1_0544cf643682408296d7a0def176b0a2_master.webp',NULL,11000,11000,'<h2 style=\"margin-left:0px;\"><span style=\"font-size:20px;\"><strong>Pate cho mèo MEOWCAT - 70gr - Nhiều Vị được làm từ các loạt thịt và rau củ giúp bổ sung các khoáng chất và phòng ngừa các loại bệnh.</strong></span></h2><p style=\"margin-left:0px;\"><span style=\"font-size:18px;\">Hộp 12 gói và mỗi gói 70g</span></p><p style=\"margin-left:0px;\"><span style=\"font-size:18px;\">&nbsp;- Thành phần tự nhiên, có thể dễ dàng trộn lỗn các thức ăn khác để giúp các bé mèo ăn trong ngon miệng hơn.</span></p><p style=\"margin-left:0px;\"><span style=\"font-size:18px;\">- Với thành phần được làm hoàn toàn từ thịt, ra củ và không chứa các chất bảo quản, phẩm màu.</span></p><p style=\"margin-left:0px;\"><span style=\"font-size:18px;\">- Các chất dinh dưỡng bổ sung nước cho cơ thể mèo và ngăn ngừa bệnh sỏi thận.</span></p><p style=\"margin-left:0px;\"><span style=\"font-size:18px;\">Bảo quản: Nơi khô ráo, thoáng mát tránh ánh nắng trực tiếp. Sử dụng trong ngày sau khi mở túi hoặc bỏ trong ngăn mát tủ lạnh.</span></p>',0,100,'Pate Mèo MEOWCAT - 70gr - Nhiều Vị',_binary '',0,0,37,8,0),(13,'xuong-cho-cho-gam-sach-rang-vegebrand-360-bone-prevent-tartar-400x400.webp',NULL,10000,8600,'<p>Xương cho chó gặm sạch răng <a href=\"https://www.petmart.vn/thuong-hieu/vegebrand\">VEGEBRAND</a> 360 Bone Prevent Tartar là thức ăn dinh dưỡng dành riêng cho các giống chó.</p><h2><strong>Lợi ích chính</strong></h2><ul><li>VEGEBRAND 360 Bone Prevent Tartar với các thành phần từ tự nhiên tạo nên hương vị thơm ngon, bổ dưỡng</li><li>Hỗ trợ rất tốt cho sức khỏe của thú cưng</li><li>Sản phẩm xương gặm giúp loại bỏ 99% những mảng bám răng cứng đầu, làm giảm mùi hôi miệng</li><li>Thúc đẩy hệ tiêu hóa và tránh được những bệnh về đường ruột</li><li>Ngăn chặn hành vi cắn phá của chó cưng</li></ul><h2><strong>Thành phần dinh dưỡng</strong></h2><p>Xương cho chó gặm sạch răng VEGEBRAND 360 Bone Prevent Tartar với các thành phần như ngũ cốc, thịt và động vật. Dẫn xuất có nguồn gốc thực vật, rau, khoáng chất. Vitamin E. Feroh Sulphate Monohydrate, Zinc Sulphate Monohydrate, Mangan Sulphate Monohydrate. Màu sắc, hương vị, chất bảo quản.</p><p>Phân tích đảm bảo: Protein thô (tối thiểu) 10%. Chất béo thô (tối thiểu) 0,4%. Sợi thô (tối đa) 4%. Tro (tối đa) 5%. Độ ẩm (tối đa) 16%. Canxi (tối thiểu) 0,05%. Photpho (tối thiểu) 0,04%. Natri (tối thiểu) 0,02%.</p><p>&nbsp;</p>',14,100,'Xương cho chó gặm sạch răng VEGEBRAND 360 Bone Prevent Tartar',_binary '',0,0,38,7,0),(14,'thuc-an-cho-cho-con-co-nho-royal-canin-mini-puppy1-400x400.webp, thuc-an-cho-cho-con-co-nho-royal-canin-mini-puppy2-400x400.jpg',NULL,255000,255000,'<p><a href=\"https://www.petmart.vn/thuc-an-cho\">Thức ăn cho chó</a> con cỡ nhỏ <a href=\"https://www.petmart.vn/thuong-hieu/royal-canin\">ROYAL CANIN</a> Mini Puppy dành cho các giống chó con dưới 10 tháng tuổi. Với công thức đặc chế riêng cho nhu cầu dinh dưỡng của chó con thuộc các giống cỡ nhỏ. Thức ăn cho chó con (các giống chó cỡ nhỏ) được nghiên cứu để cung cấp dinh dưỡng theo nhu cầu thực tế của chó con.</p><p>Duy trì sức đề kháng cho chó con: chất chống oxy hóa CELT. Hỗ trợ hệ tiêu hóa hoạt động ổn định: L.I.P, đường FOS. Cung cấp dinh dưỡng toàn diện cho chó: chế biến theo công thức cung cấp năng lượng cao.</p><h2><strong>Lợi ích chính</strong></h2><p>ROYAL CANIN Mini Puppy có dạng viên hình tam giác phù hợp với cấu tạo răng của chó, giảm nguy cơ hình thành mảng bám răng ở chó. Kích thước hạt phù hợp cỡ răng, thông qua việc cọ xát vào răng giúp bảo vệ răng miệng cho chó.</p><p>Duy trì hệ tiêu hóa khỏe mạnh. Men tiêu hóa L.I.P hỗ trợ hấp thu chất <a href=\"https://www.petmart.vn/dinh-duong-cho\">dinh dưỡng cho chó</a>, cân bằng hệ vi sinh đường ruột, giảm bớt lượng phân và mùi nhẹ hơn. Đường tự nhiên FOS: cân bằng hệ vi sinh đường ruột. Gia tăng số lượng lợi khuẩn, bảo vệ đường ruột khỏi vi khuẩn có hại.</p><h2><strong>Hướng dẫn cho ăn</strong></h2><p>Thức ăn <a href=\"https://www.petmart.vn/hat-cho-cho\">hạt cho chó</a> con cỡ nhỏ ROYAL CANIN Mini Puppy tạo thói quen ăn uống cho chó. Dựa theo tuổi của chó, cần cho ăn một ngày 3 lần vào các giờ cố định. Cho ăn tại một chỗ để tạo thói quen tốt cho chó. Nên cho chó ăn thức ăn chế biến riêng, không cho ăn thức ăn thừa của người. Vì thức ăn của người có nhiều thành phần khiến chó bị rối loạn tiêu hóa, dễ bị bệnh béo phì. Bảo đảm cung cấp đủ nước uống cho chó. Nếu thấy nước bị chó làm bẩn, cần thay nước mới ngay lập tức.</p><p>Khi muốn đổi thức ăn mới cho chó, có thể trộn lẫn thức ăn cũ và mới khi cho ăn. Tăng dần tỉ lệ trong vòng 7 ngày. Đột ngột thay đổi loại thức ăn mới có thể gây mất cân bằng hệ tiêu hóa. Chó dễ bị khó tiêu và đi ngoài.</p><h2><strong>Một số lưu ý</strong></h2><p>Chó con mới mọc răng cần cho ăn thức ăn mềm, vì vậy trước khi cho ăn cần ngâm trong nước ấm 40°C-60°C. Sau 15′ thức ăn mềm là có thể cho ăn.</p><p>Chó con còn nhỏ không tiêu hóa được quá nhiều thức ăn. Chó con mới cai sữa ngày cho ăn 4 lần, dưới 4 tháng tuổi ngày cho ăn 3 lần. Tham khảo khuyến cáo trên bao bì sản phẩm để giúp chó con duy trì trạng thái tốt nhất. Hạn sử dụng 18 tháng kể từ ngày sản xuất.</p>',0,91,'Thức ăn cho chó con cỡ nhỏ ROYAL CANIN Mini Puppy',_binary '',2,9,25,7,9),(15,'banh-thuong-cho-cho-vi-thit-bo-vegebrand-orgo-freshening-biscuit-bacon-beef-400x400.webp',NULL,25000,25000,'<p><a href=\"https://www.petmart.vn/banh-thuong-cho\">Bánh thưởng cho chó</a> vị thịt bò <a href=\"https://www.petmart.vn/thuong-hieu/vegebrand\">VEGEBRAND</a> Orgo Freshening Biscuit Bacon Beef có tác dụng làm sạch răng cho chó vị thịt bò. Sản phẩm có chứa các thành phần bạc hà tự nhiên kết hợp với hương vị thịt bò, có khả năng loại bỏ các vi khuẩn gây hôi miệng cho chú chó của bạn một cách nhanh chóng. Sản phẩm có thể kết hợp dùng để huấn luyện.</p><h2><strong>Lợi ích chính</strong></h2><p>Đặc biệt bánh thưởng cho chó vị thịt bò VEGEBRAND Orgo Freshening Biscuit Bacon Beef có thêm thịt bò tươi ngon, thông qua quá trình sấy khô tỉ mỉ, vẫn có thể đảm bảo được thành phần dinh dưỡng có trong thịt bò, bổ sung cho thú cưng lượng protein và năng lượng cần thiết. Đặc biệt có thêm chút đường oligose, có thể chăm sóc hệ thống đường ruột và dạ dày, hiệu quả trong việc loại bỏ mùi lạ từ phân và mùi hôi miệng. Với độ cứng vừa phải, có thể đáp ứng hoàn toàn nhu cầu nhai của thú cưng, làm sạch hiệu quả bề mặt răng và mát sa nướu răng.</p><h2><strong>Thành phần dinh dưỡng</strong></h2><p>Protein ≥ 25%,&nbsp;Chất béo ≤ 4%, Bột tro ≤ 5%,&nbsp;Chất xơ ≤ 5%,&nbsp;Hàm lượng nước ≤ 15%,&nbsp;Thịt bò,&nbsp;Gelatin thực phẩm,&nbsp;Protein từ đậu nành,&nbsp;Tinh bột gạo,&nbsp;Glycerin.</p><p>Thịt bò: giàu Protein, Axit amin, Vitamin B6…hàm lượng chất béo thấp, các chú chó có thể ăn để nâng cao khả năng miễn dịch, bổ sung năng lượng, tăng trưởng cơ, không bị béo phì. Đậu nành: giàu chất xơ, hỗ trợ đường ruột hoạt động tốt hơn, chứa nhiều nguyên tố vi lượng giúp lông sáng đẹp.</p>',0,100,'Bánh thưởng cho chó vị thịt bò VEGEBRAND Orgo Freshening Biscuit Bacon Beef',_binary '',0,0,38,7,0),(16,'pate-cho-cho-vi-thit-bo-iris-one-care-beef100g-400x400.webp',NULL,35000,35000,'<p><a href=\"https://www.petmart.vn/pate-cho\">Pate cho chó</a> vị thịt bò <a href=\"https://www.petmart.vn/thuong-hieu/iris-ohyama\">IRIS OHYAMA</a> One Care Beef là sản phẩm dành cho tất cả giống chó. Với thành phần hoàn toàn từ tự nhiên và thịt bò.</p><h2><strong>Lợi ích chính</strong></h2><ul><li>Đặc biệt với công thức chế biến riêng tạo nên khẩu phần ăn ngon miệng cho thú cưng.</li><li>Bổ sung Protein đảm bảo một sức khỏe toàn diện hơn.</li><li>Tạo nên cảm giác thèm ăn cho cún cưng.</li><li><span style=\"color:rgb(85,85,85);font-size:14.4px;\">Pate cho chó vị thịt bò IRIS One Care Beef làm hài lòng cả những chú chó kén ăn nhất.</span></li></ul><h2><strong>Thành phần dinh dưỡng</strong></h2><p>Pate cho chó vị thịt bò IRIS OHYAMA One Care Beef bao gồm các thành phần như thịt gà, thịt bò, cà rốt, đậu hà lan, gạo.</p>',0,100,'Pate cho chó vị thịt bò IRIS OHYAMA One Care Beef',_binary '',0,0,39,7,0),(17,'pate-cho-cho-nuoc-sot-vi-thit-bo-pedigree-pouch-beef-400x400.jpg, pate-cho-cho-nuoc-sot-vi-thit-bo-pedigree-pouch-beef1-400x400.jpg, pate-cho-cho-nuoc-sot-pedigree-400x400.jpg',NULL,25000,25000,'<p><a href=\"https://www.petmart.vn/pate-cho\">Pate cho chó</a> nước sốt vị thịt bò <a href=\"https://www.petmart.vn/thuong-hieu/pedigree\">PEDIGREE</a> Pouch Beef với hương vị kích thích dành cho cún biếng ăn, có thể trộn với cơm, hạt khô để tạo mùi cho thức ăn. Ngoài ra trong trường hợp không có thức ăn sẵn cho vật nuôi, có thể dùng để cho cún ăn trực tiếp.</p><h2><strong>Lợi ích chính</strong></h2><ul><li>Sản phẩm cũng được coi là thức ăn phụ bổ sung dinh dưỡng cho thú cưng</li><li>Cung cấp nguồn dinh dưỡng tối đa giúp cho thú cưng của bạn luôn khỏe mạnh</li><li>Đồng thời, kích thích sự ăn ngon. Mang lại cảm giác thèm ăn cho cún cưng. Phù hợp với cả những chú chó biếng ăn nhất</li><li>Tăng cường khả năng miễn dịch</li><li>Thúc đẩy phát triển răng</li><li>Nâng cao khả năng tiêu hóa</li><li>Thúc đẩy sự phát triển xương</li><li>Thúc đẩy da và lông chắc khỏe, mềm mượt hơn</li></ul><h2><strong>Thành phần dinh dưỡng</strong></h2><p>Pate cho chó nước sốt vị thịt bò PEDIGREE Pouch Beef gồm các thành phần như thịt gà, gan thịt bò, cà rốt, nước thịt, Gluten lúa mì, huyết tương thịt bò, chất xơ, đậu nành, Axit Amin, Vitamin, đường, chất tạo màu.</p><p>Sản phẩm cung cấp cho chú chó của bạn dinh dưỡng cần thiết. Được tuyển chọn từ những miếng thịt tươi ngon nhất cung cấp lượng Vitamin, khoáng chất và Axit béo cho sự phát triển của chú chó nhà bạn. Pate Pedigree vị thịt bò cho chó cưng được ninh hầm kĩ, để cho chú chó nhỏ của bạn cảm nhận được hương vị tốt nhất.</p>',0,1,'Pate cho chó nước sốt vị thịt bò PEDIGREE Pouch Beef',_binary '',0,0,40,7,0),(18,'thuc-an-cho-cho-poodle-con-royal-canin-poodle-puppy2-400x400.jpg, thuc-an-cho-cho-poodle-con-royal-canin-poodle-puppy1-400x400.webp',NULL,185000,185000,'<p><a href=\"https://www.petmart.vn/thuc-an-cho\">Thức ăn cho chó</a> Poodle con <a href=\"https://www.petmart.vn/thuong-hieu/royal-canin\">ROYAL CANIN</a> Poodle Puppy dành riêng cho tất cả các giống chó Teacup, Tiny Poodle, Toy Poodle, Standard Poodle dưới 10 tháng tuổi.</p><h2><strong>Lợi ích chính</strong></h2><p>Thức ăn cho chó Poodle con ROYAL CANIN Poodle Puppy với công thức đặc chế riêng cho nhu cầu dinh dưỡng của chó Poodle trong giai đoạn mới cai sữa. Sản phẩm được chế biến theo công thức hỗ trợ phát triển toàn diện, tăng sức đề kháng của chó con. Đồng thời tăng khả năng tư duy, vận động của chó con, đẩy nhanh quá trình phát triển võng mạc của chó.</p><p>Sản phẩm được chế biến làm mượt lông, dưỡng da cho chó Poodle. PINCH thúc đẩy sự hình thành axit amide, giúp bảo vệ da khỏi các tác nhân gây hại. OMEGA 3 và 6: kích thích tuyến bã nhờn dưới da tiết dầu.</p><p>Tăng sức đề kháng cho chó con. Chất chống oxy hóa giúp chó con tăng khả năng miễn dịch, giúp cho con lớn lên khỏe mạnh.</p><p>Hạt thức ăn hình vuông được chế biến đặc biệt giảm nguy cơ hình thành sỏi thận ở chó. Kích thước hạt phù hợp cỡ răng của chó, thông qua việc cọ xát vào răng giúp bảo vệ răng miệng cho chó. Size hạt phù hợp cấu tạo khoang miệng của chó, thuận lợi cho tiêu hóa.</p><p>Thành phần bao gồm lòng trắng trứng và các vi sinh vật giúp nhuận tràng, tốt cho hệ tiêu hóa. Đặc biệt 90% protein L.I.P hỗ trợ chó hấp thu chất dinh dưỡng, cân bằng hệ vi sinh đường ruột, giảm bớt lượng phân và mùi nhẹ hơn. Gia tăng số lượng lợi khuẩn, bảo vệ đường ruột khỏi vi khuẩn có hại.</p><h2><strong>Hướng dẫn sử dụng</strong></h2><p>Thức ăn <a href=\"https://www.petmart.vn/hat-cho-cho\">hạt cho chó</a> Poodle con ROYAL CANIN Poodle Puppy tạo thói quen ăn uống cho chó con. Dựa theo tuổi của chó, cần cho ăn một ngày 3 lần vào các giờ cố định. Cho ăn tại một chỗ để tạo thói quen tốt cho chó. Nên cho chó ăn thức ăn chế biến riêng, không cho ăn thức ăn thừa của người. Vì thức ăn của người có nhiều thành phần khiến chó bị rối loạn tiêu hóa, dễ bị bệnh béo phì. Bảo đảm cung cấp đủ nước uống cho chó. Nếu thấy nước bị chó làm bẩn, cần thay nước mới ngay lập tức.</p><p>Khi muốn đổi thức ăn mới cho chó, có thể trộn lẫn thức ăn cũ và mới khi cho ăn. Tăng dần tỉ lệ trong vòng 7 ngày. Đột ngột thay đổi loại thức ăn mới có thể gây mất cân bằng hệ tiêu hóa. Chó dễ bị khó tiêu và đi ngoài.</p><p>Công thức phân chia lượng thức ăn theo cân nặng có thể tham khảo trên bao bì.&nbsp;Hạn sử dụng 18 tháng kể từ ngày sản xuất.</p>',0,0,'Thức ăn cho chó Poodle con ROYAL CANIN Poodle Puppy',_binary '',0,0,25,7,1),(19,'xuong-cho-cho-vi-thit-bo-le-vegebrand-orgo-nutrients-beef-400x400.webp',NULL,25000,25000,'<p>Xương cho chó vị thịt bò <a href=\"https://www.petmart.vn/thuong-hieu/vegebrand\">VEGEBRAND</a> Orgo Nutrients Beef dành cho các giống chó có kích thước trung bình có chứa vị thịt bò.</p><h2><strong>Lợi ích chính</strong></h2><ul><li>Hiệu quả dinh dưỡng cao tăng cường thể chất, bổ sung canxi</li><li>Răng nướu chắc khỏe, giảm hình thành mảng bám cao răng và làm sạch răng</li><li>Sản phẩm xương gặm có thể sử dụng cho ăn trực tiếp hoặc cũng có thể làm đồ ăn thưởng cho chú chó của bạn</li><li>Xương cho chó vị thịt bò VEGEBRAND Orgo Nutrients Beef sử dụng thịt bò tươi ngon, có lượng protein cao, có hiệu quả thúc đẩy cơ thịt, bổ sung năng lượng</li><li>Sản phẩm vị thịt bò, có chứa đầy đủ vitamin B6, có thể tăng cường khả năng miễn dịch</li><li>Thúc đẩy việc hợp thành và chuyển hóa Protein, từ đó hỗ trợ phục hồi thể trạng yếu hoặc sau khi bị bệnh</li><li>Thiết kế hình xương có thể đáp ứng tập tính nhai của những chú chó, mát-xa răng hiệu quả, nướu chắc khỏe, nâng cao khả năng nhai của chó</li></ul><h2><strong>Thành phần dinh dưỡng</strong></h2><p><strong>&nbsp;</strong>Protein ≥ 18%,&nbsp;Lysine ≥ 0.3%,&nbsp;Bột tro ≤ 5%,&nbsp;Chất xơ ≤ 5%,&nbsp;Hàm lượng nước ≤ 15%,&nbsp;Muối 0.3-1.5%.</p><p>Xương cho chó vị thịt bò VEGEBRAND Orgo Nutrients Beef được kiểm soát nghiêm ngặt các nguyên liệu tự nhiên từ những nguyên liệu tươi ngon, không chứa Hoocmon và chất kháng sinh, trước khi chế biến, kiểm tra các tiêu chuẩn lý hóa như protein, chất béo, tiêu chuẩn sinh vật như chất vi sinh, hợp lí mới bắt đầu tiến hành sản xuất. Sản phẩm hình xương giàu chất dinh dưỡng được chế biến từ thịt bò tươi ngon chất lượng ngon, giàu vitamin và protein, có thể tăng cường thể chất cũng như làm sạch răng.</p><ul><li>Thịt bò chất lượng tốt.</li><li>Lượng Protein cao.</li><li>Giàu nguyên tố vi lượng</li><li>Sử dụng Gelatin thực phẩm</li><li>Không có chất bảo quản, chất kích thích ăn, sắc tố.</li><li>Tuyệt đối không cho chất hóa học.</li></ul>',0,1,'Xương cho chó vị thịt bò VEGEBRAND Orgo Nutrients Beef',_binary '',0,0,38,7,0),(20,'xuong-canxi-cho-cho-vegebrand-orgo-high-calcium-cheese-400x400.webp',NULL,50000,50000,'<p>Xương Canxi cho chó <a href=\"https://www.petmart.vn/thuong-hieu/vegebrand\">VEGEBRAND</a> Orgo High Calcium Cheese có hàm lượng canxi cao, đáp ứng tốt cho mọi giống chó ở mọi độ tuổi khác nhau. Với thành phần pho mát chất lượng cao từ nguyên liệu thô cùng công thức sữa thơm ngon có hàm lượng canxi cao giúp cho sự phát triển xương tốt hơn, răng chắc khỏe, loại bỏ mảng bám cao răng, giúp răng trắng hơn, không gây mùi hôi khó chịu và cải thiện khả năng gặm – nhai của cún cưng.</p><h2><strong>Lợi ích chính</strong></h2><ul><li>Xương Canxi cho chó VEGEBRAND Orgo High Calcium Cheese với 4 công hiệu – hiệu năng khi sử dụng.</li><li>Xương gặm sử dụng nguyên liệu tươi ngon, an toàn cho thú cưng.</li><li>Tiêu chuẩn sản xuất nghiệm ngặt, không có chất phụ gia.</li><li>Làm sạch răng: với thiết kế hình xương có thể thúc đẩy khả năng nhai của thú&nbsp;cưng, hiệu quả trong việc làm sạch răng, loại bỏ cao răng, mảng bám, không còn&nbsp;tình trạng răng vàng, nướu chắc khỏe, nâng cao khả năng nhai.</li><li>Phù hợp khẩu vị: với hương liệu từ phomat và sữa được phơi bằng gió tự nhiên,&nbsp;mùi vị đậm đà, độ dẻo cao, thích hợp làm đồ ăn để khen thưởng.</li><li>Giúp xương chắc khỏe: với hàm lượng canxi cao giúp xương thú cưng luôn khỏe,&nbsp;răng chắc.</li><li>Bổ sung Canxi: tuyển chọn từ phomat tự nhiên, kết hợp với sữa, khiến cho hàm lượng Canxi cao, hiệu quả bổ sung canxi tốt.</li></ul><h2><strong>Thành phần dinh dưỡng</strong></h2><p>Xương Canxi cho chó VEGEBRAND Orgo High Calcium Cheese bao gồm các thành phần như sữa bò tươi, pho-mát, tinh bột gạo, Gelatin thực phẩm, Glycerin, chất xơ cùng tỉ lệ nguyên liệu Protein ≥ 15%, Chất béo ≤2%, Chất xơ ≤ 4%, Tro ≤ 4%, Hàm lượng nước ≤ 15%.</p>',0,100,'Xương Canxi cho chó VEGEBRAND Orgo High Calcium Cheese',_binary '',0,0,38,7,0),(21,'pate-cho-cho-ho-tro-chuc-nang-gan-va-sang-mat-iris-benefit-for-eyes-liver-400x400.jpg',NULL,35000,35000,'<p><a href=\"https://www.petmart.vn/pate-cho\">Pate cho chó</a> hỗ trợ chức năng gan và sáng mắt <a href=\"https://www.petmart.vn/thuong-hieu/iris-ohyama\">IRIS OHYAMA</a> Benefit For Eyes &amp; Liver là sản phẩm dành cho tất cả giống chó. Với thành phần hoàn toàn từ tự nhiên.</p><h2><strong>Lợi ích chính</strong></h2><ul><li>Đặc biệt với công thức chế biến riêng hỗ trợ rất tốt cho mắt.</li><li>Cải thiện được chức năng gan cho thú cưng.</li><li>Tạo nên một sức khỏe toàn diện hơn.</li><li>Hỗ trợ chức năng gan.</li><li>Duy trì đôi mắt sáng rõ.</li></ul><h2><strong>Thành phần dinh dưỡng</strong></h2><p>Pate cho chó hỗ trợ chức năng gan và sáng mắt IRIS OHYAMA Benefit For Eyes &amp; Liver gồm các thành phần ức gà, hoa cúc, chất béo, Protein, chất xơ…</p>',0,0,'Pate cho chó hỗ trợ chức năng gan và sáng mắt IRIS OHYAMA Benefit For Eyes & Liver',_binary '',1,4,39,7,1),(22,'thuc-an-cho-cho-poodle-mkb-all-life-stages-formula-nutrition-400x400.jpg',NULL,315000,315000,'<p><a href=\"https://www.petmart.vn/thuc-an-cho\">Thức ăn cho chó</a> Poodle <a href=\"https://www.petmart.vn/thuong-hieu/mkb\">MKB</a> All Life Stages Formula Nutrition, dành riêng cho chó Poodle, bao gồm tất cả các giai đoạn phát triển.</p><h2><strong>Lợi ích chính</strong></h2><p>Thức ăn cho chó Poodle MKB All Life Stages Formula Nutrition giúp bổ sung tỷ lệ cân đối giữa các chất dinh dưỡng. Đảm bảo sự phát triển của cún con qua tất cả các giai đoạn phát triển. Bao gồm cả những chú chó con, chó trưởng thành, chó mang thai, chó cho con bú và chó già. Đặc biệt, loại thức ăn này sử dụng tỷ lệ dầu thấp, hạt thức ăn khô không chứa dầu mỡ. Những lợi ích khi chó Poodle sử dụng loại thức ăn này:</p><ul><li>Cung cấp nguồn dinh dưỡng theo đúng tiêu chuẩn cho giống Poodle.</li><li>Hạn chế và ngăn ngừa sự chảy nước mắt gây vàng ố lông ở khóe mắt.</li><li>Poodle cần công thức dinh dưỡng độc quyền</li><li>Công thức chế biến ít muối và ít chất béo, hàm lượng muối được điều chỉnh khoa học.</li><li>Ngăn ngừa mảng bám răng hiệu quả với hàm lượng Canxi được điều chỉnh khoa học.</li><li>Chăm sóc và nuôi dưỡng bộ lông cho chó Poodle mềm mượt, sáng bóng và không xơ rối</li><li>Thúc đẩy tiêu hóa an toàn, thích hợp cho cả chó con.</li><li>Khử mùi phân tối đa.</li></ul>',0,100,'Thức ăn cho chó Poodle MKB All Life Stages Formula Nutrition',_binary '',0,0,41,7,0),(23,'xuong-gam-cho-cho-poodle-vegebrand-360-for-poodles-bone-400x400.jpg',NULL,50000,50000,'<p>Xương gặm cho chó Poodle <a href=\"https://www.petmart.vn/thuong-hieu/vegebrand\">VEGEBRAND</a> 360 For Poodles Bone vị thịt vịt phù hợp với giống chó Poodle trong mọi giai đoạn phát triển.</p><h2><strong>Lợi ích chính</strong></h2><ul><li>Xương gặm cho chó Poodle VEGEBRAND 360 For Poodles Bone với thành phần dinh dưỡng từ thiên nhiên không độc hại.</li><li>Có thể bổ sung dinh dưỡng.</li><li>Nhai lâu tăng hiệu quả matxa răng.</li><li>Loại bỏ mảng bám và cao răng.</li><li>Thúc đẩy hoạt động đường ruột, hỗ trợ tiêu hóa.</li><li>Xương gặm loại bỏ vị khác lạ ở miệng.</li><li>Có thể làm đồ ăn để huấn luyện.</li><li>Hấp dẫn thú cưng, làm tinh thần thoải mái, không còn cảm giác cô đơn.</li></ul><h2><strong>Thành phần dinh dưỡng</strong></h2><p>Xương gặm cho chó Poodle VEGEBRAND 360 For Poodles Bone bao gồm các thành phần như lúa mạch, tinh bột gạo, thịt vịt, khoáng chất, Gluten, đường trái cây, Glycerin.</p>',0,100,'Xương gặm cho chó Poodle VEGEBRAND 360 For Poodles Bone',_binary '',0,0,38,7,0),(24,'pate-cho-cho-ho-tro-he-tieu-hoa-iris-benefit-for-enteric-canal-400x400.jpg',NULL,35000,30100,'<p><a href=\"https://www.petmart.vn/pate-cho\">Pate cho chó</a> hỗ trợ hệ tiêu hóa <a href=\"https://www.petmart.vn/thuong-hieu/iris-ohyama\">IRIS OHYAMA</a> Benefit For Enteric Canal là sản phẩm dành cho tất cả giống chó.</p><h2><strong>Lợi ích chính</strong></h2><ul><li>Với thành phần hoàn toàn từ tự nhiên, không chất bảo quản, phụ gia độc hại.</li><li>Hỗ trợ hệ tiêu hóa cho chó cưng. Bảo vệ nhu động ruột.</li><li>Đặc điểm duy trì thể trạng lý tưởng cho thú cưng.</li><li>Đặc biệt với công thức chế biến riêng tạo nên khẩu phần ăn ngon miệng.</li><li>Cung cấp đầy đủ chất đạm, béo, vitamin B1 và khoáng chất.</li><li>Kiểm soát trọng lượng của chó.</li><li>Bạn có thể dùng làm thức ăn chính hoặc ăn kết hợp với các loại thức ăn khác cũng được.</li></ul><h2><strong>Thành phần dinh dưỡng</strong></h2><p>Pate cho chó hỗ trợ hệ tiêu hóa IRIS OHYAMA Benefit For Enteric Canal gồm các thành phần như ức gà, bí ngô, chất béo, chất xơ, nước, Protein, bột Konjac…</p>',14,100,'Pate cho chó hỗ trợ hệ tiêu hóa IRIS OHYAMA Benefit For Enteric Canal',_binary '',0,0,39,7,0),(25,'thuc-an-cho-cho-poodle-truong-thanh-royal-canin-poodle-adult1.jpg, thuc-an-cho-cho-poodle-truong-thanh-royal-canin-poodle-adult2.jpg',NULL,155000,155000,'<p><a href=\"https://www.petmart.vn/thuc-an-cho\">Thức ăn cho chó</a> Poodle trưởng thành <a href=\"https://www.petmart.vn/thuong-hieu/royal-canin\">ROYAL CANIN</a> Poodle Adult dành riêng cho tất cả các giống chó Teacup, Tiny Poodle, Toy Poodle, Standard Poodle từ 10 tháng tuổi trở lên.</p><h2><strong>Lợi ích chính</strong></h2><p>ROYAL CANIN Poodle Adult với công thức đặc chế riêng cho nhu cầu dinh dưỡng của chó Poodle, đặc trị các vấn đề về răng và lông ở chó Poodle. PINCH: kích thích hình thành tế bào da, giúp da luôn khỏe mạnh và tránh các bệnh về da thường gặp. OMEGA 3 và 6: kích thích tuyến bã nhờn dưới da tiết dầu, tránh các tác nhân gây hại cho da.</p><p>Hạt thức ăn cho chó Poodle trưởng thành ROYAL CANIN Poodle Adult có dạng hình vuông. Được chế biến đặc biệt giảm khả năng tạo mảng bám trên răng. Kích thước hạt phù hợp cấu tạo khoang miệng của chó. Thông qua việc ma sát với răng giúp chó tự làm sạch răng miệng. Thành phần thức ăn kết hợp với ion có sẵn trong nước bọt làm giảm vi khuẩn sinh ra trên răng. Cung cấp canxi làm chắc khỏe răng.</p><p>Thành phần bao gồm lòng trắng trứng và các vi sinh vật giúp nhuận tràng, tốt cho hệ tiêu hóa. Đặc biệt 90% protein L.I.P hỗ trợ chó hấp thu chất dinh dưỡng, cân bằng hệ vi sinh đường ruột, giảm bớt lượng phân và mùi nhẹ hơn. Gia tăng số lượng lợi khuẩn, bảo vệ đường ruột khỏi vi khuẩn có hại.</p><h2><strong>Hướng dẫn sử dụng</strong></h2><p>Thức ăn <a href=\"https://www.petmart.vn/hat-cho-cho\">hạt cho chó</a> Poodle trưởng thành ROYAL CANIN Poodle Adult tạo thói quen ăn uống cho chó con. Dựa theo tuổi của cho, cần cho ăn một ngày 3 lần vào các giờ cố định. Cho ăn tại một chỗ để tạo thói quen tốt cho chó. Nên cho chó ăn thức ăn chế biến riêng, không cho ăn thức ăn thừa của người. Vì thức ăn của người có nhiều thành phần khiến chó bị rối loạn tiêu hóa, dễ bị bệnh béo phì. Bảo đảm cung cấp đủ nước uống cho chó. Nếu thấy nước bị chó làm bẩn, cần thay nước mới ngay lập tức.</p><p>Khi muốn đổi thức ăn mới cho chó, có thể trộn lẫn thức ăn cũ và mới khi cho ăn. Tăng dần tỉ lệ trong vòng 7 ngày. Đột ngột thay đổi loại thức ăn mới có thể gây mất cân bằng hệ tiêu hóa. Chó dễ bị khó tiêu và đi ngoài.</p><p>Công thức phân chia lượng thức ăn theo cân nặng có thể tham khảo trên bao bì.&nbsp;Hạn sử dụng 18 tháng kể từ ngày sản xuất.</p>',0,1,'Thức ăn cho chó Poodle trưởng thành ROYAL CANIN Poodle Adult',_binary '',0,0,25,7,0),(26,'binh-sua-cho-cho-meo-so-sinh-bobo-pet-milk-bottle-400x400.webp',NULL,50000,50000,'<p>Bình sữa cho chó mèo sơ sinh <a href=\"https://www.petmart.vn/thuong-hieu/bobopet\">BOBO</a> Pet Milk Bottle được dùng cho tất cả giống chó và mèo.</p><h2><strong>Lợi ích chính</strong></h2><p>Bình sữa cho chó mèo sơ sinh BOBO Pet Milk Bottle được thiết kế độc đáo với núm vú giả hoàn toàn phù hợp với đặc tính sinh lý của chó mèo. Do miệng mèo con, chó con nhỏ hơn của trẻ em sơ sinh rất nhiều, khi cho mèo con, chó con uống sữa phải sử dụng thiết kế miệng nhỏ. Sau khi sử dụng có thể dùng bàn chải trong gói làm &nbsp;sạch bình sữa. Sản phẩm này rất thích hợp cho chó mới sinh, khi sữa của chó mẹ không đủ có thể dùng sản phầm này đựng sữa cho chó con, khi chó con bị bệnh cũng sử dụng để cho uống thuốc.</p><h2><strong>Hướng dẫn sử dụng</strong></h2><ul><li>Trước khi sử dụng nên châm một lỗ nhỏ trên miệng bình, có thể tham khảo cách sau đây:&nbsp;dùng kim nhỏ đâm lên miệng bình cao su mềm, hoặc dùng dao rạch một chữ thập lên&nbsp;trên. Nhẹ nhàng ấn bình sữa để nước nhỏ ra một cách ổn định hoặc chảy ra tia nước nhỏ.</li><li>Trước và sau khi sử dụng sản phẩm này nên dùng nước ấm tiệt trùng làm sạch bình sữa,&nbsp;miệng bình.</li><li>Trong sản phẩm này có bàn chải bình sữa chuyên dụng, có thể nhẹ nhàng thuận tiện làm&nbsp;sạch bên trong bình.</li><li>Thân bình có thiết kế vạch nấc dễ dàng nắm được lượng thực phẩm cho ăn, tránh cho uống&nbsp;quá nhiều hoặc quá ít</li><li>Kết cấu miệng bình bền chắc, chất liệu cao su có tính đàn hồi, an toàn bảo vệ môi trường.</li></ul><p>Trọng lượng: 50g. Dung tích: 50ml. Chất liệu: nhựa + cao su mềm.</p><p>Khi mua sản phẩm bình bú sữa cho chó mèo sơ sinh tặng kèm bàn chải bình sữa, bảo đảm bình sữa luôn sạch sẽ tặng thêm một miệng bình (núm) có thể thay đổi, sử dụng luân phiên. Tránh bị các bệnh truyền nhiễm.</p>',0,1,'Bình sữa cho chó mèo sơ sinh BOBO Pet Milk Bottle',_binary '',0,0,42,9,0),(27,'zenith-puppy-chicken-potato-400x400.jpg',NULL,240000,240000,'<p><a href=\"https://www.petmart.vn/thuc-an-cho\">Thức ăn cho chó</a> con hạt mềm <a href=\"https://www.petmart.vn/thuong-hieu/zenith\">ZENITH</a> Puppy Chicken &amp; Potato được chế biến từ các nguyên liệu tươi sạch như thịt cừu, thịt nạc gà rút xương, gạo lứt, yến mạch và dầu cá hồi, cung cấp độ ẩm cao và lượng muối thấp. Sản phẩm không chỉ thơm ngon, dễ nhai và dễ tiêu hóa mà còn đặc biệt tốt cho sức khỏe và sự phát triển của chó con dưới 1 tuổi.</p><h2><strong>Đặc điểm nổi bật</strong></h2><ul><li>Không chứa ngũ cốc, không gây dị ứng.</li><li>Thành phần chính từ thịt cừu, bột gà, gạo lứt và yến mạch.</li><li>Giúp giảm mùi phân và mùi cơ thể, hỗ trợ tiêu hóa và tăng cường sức đề kháng.</li></ul><h2><strong>Lợi ích chính</strong></h2><ul><li><strong>Cung cấp dinh dưỡng thiết yếu:</strong> Sản phẩm giàu dinh dưỡng với EPA, DHA giúp phát triển trí não và tăng trí thông minh. Omega 3, 6 hỗ trợ sức khỏe mắt và lông. Thịt gà rút xương giàu protein, ít béo, hỗ trợ xương chắc khỏe và giúp giảm căng thẳng.</li><li><strong>Tăng cường sức đề kháng: </strong>Zenith Puppy chứa β-glucan, một loại chất xơ thực vật giúp thúc đẩy sự thèm ăn, tăng cường khả năng miễn dịch. Axit béo và lecithin giúp bảo vệ cơ thể khỏi bệnh tật. Sản phẩm còn được bổ sung chondroitin và glucosamine giúp xương khớp phát triển khỏe mạnh.</li><li><strong>Hỗ trợ hệ tiêu hóa: </strong>Không chứa ngũ cốc như đậu, bắp và lúa mì – nguyên nhân chính gây dị ứng. Bột dừa, chất xơ, vitamin và khoáng chất giúp ức chế vi sinh vật có hại trong đường ruột, ngăn ngừa tiêu chảy và cải thiện sức khỏe tiêu hóa cho chó con.</li><li><strong>Giảm thiểu mùi phân và mùi cơ thể: </strong>Chiết xuất từ cây ngọc giá giúp giảm mùi phân, trong khi gạo lứt và thịt cừu giàu calo và ít mỡ giúp bảo vệ sức khỏe tim mạch và đào thải chất độc ra khỏi cơ thể.</li><li><strong>Duy trì da và lông khỏe mạnh: </strong>Dầu cá hồi, dầu thực vật, dầu dừa và dầu hạt hướng dương cung cấp Omega 3, giúp duy trì làn da khỏe mạnh, lông mềm mượt, giảm rụng lông và ngăn ngừa các vấn đề về da như viêm nhiễm, ve rận, và bọ chét.</li></ul><p>ZENITH Puppy Chicken Potato được chế biến dưới dạng bán ẩm với hạt mềm dễ nhai, dễ tiêu hóa, mang lại dinh dưỡng toàn diện cho chó con trong giai đoạn phát triển quan trọng.</p>',0,97,'Thức ăn cho chó con hạt mềm ZENITH Puppy Chicken Potato',_binary '',0,0,43,7,0),(28,'xuong-gam-sach-rang-cho-cho-vegebrand-orgo-freshening-peppermint-400x400.jpg, xuong-gam-sach-rang-cho-cho-vegebrand-orgo-freshening-peppermint2-400x400.jpg',NULL,105000,105000,'<p>Xương gặm sạch răng cho chó <a href=\"https://www.petmart.vn/thuong-hieu/vegebrand\">VEGEBRAND</a> Orgo Freshening Peppermint là sản phẩm dinh dưỡng dành cho chó trên 5 tháng tuổi.</p><h2><strong>Lợi ích chính</strong></h2><ul><li>Xương cho chó thơm miệng Orgo 100% Pure Nature Freshening Peppermint với tinh chất bạc hà the mát.</li><li>Có khả năng loại bỏ các vi khuẩn gây hôi miệng.</li><li>Giúp chú chó của bạn sẽ rất thích thú khi nhai.</li><li>Hình dạng chiếc xương càng làm tăng tính thích thú ở chúng.</li><li>Xương gặm Orgo 100% Pure Nature Freshening Peppermint loại bỏ mảng bám trên răng của thú cưng.</li><li>Cho chú chó bốn chân của bạn một hơi thở tươi mát hơn bao giờ hết.</li><li>Làm sạch răng và bảo vệ cao răng cho chú chó của bạn một cách nhanh chóng.</li></ul><h2><strong>Thành phần dinh dưỡng</strong></h2><p>Xương gặm sạch răng cho chó VEGEBRAND Orgo Freshening Peppermint bao gồm các thành phần như bạc hà, gelatin, tinh bột, Oligosacarit, gia vị tự nhiên, axit béo ăn được, các nguyên tố đa sắc khác…</p>',0,99,'Xương gặm sạch răng cho chó VEGEBRAND Orgo Freshening Peppermint',_binary '',0,0,38,7,1),(29,'xuong-cho-cho-gam-vi-sua-vegebrand-360-calcium-milk-bone-400x400.jpg',NULL,50000,50000,'<p>Xương cho chó gặm vị sữa <a href=\"https://www.petmart.vn/thuong-hieu/vegebrand\">VEGEBRAND</a> 360 Calcium Milk Bone thích hợp với: chó kích thước nhỏ, kích thước trung bình và kích thước lớn. Sản phẩm giống như <a href=\"https://www.petmart.vn/banh-thuong-cho\">bánh thưởng cho chó</a>, có thể sử dụng: cho ăn trực tiếp, hoặc có thể dùng làm đồ ăn khi huấn luyện hoặc khen thưởng.</p><h2><strong>Lợi ích chính</strong></h2><ul><li>VEGEBRAND 360 Calcium Milk Bone với thiết kế hình lập thể: hình lập thể có lợi cho việc làm sạch răng, còn có hiệu quả loại bỏ mảng bám cứng đầu, làm sạch bộ phận nướu.</li><li>Nguyên liệu tự nhiên: đặc biệt có thêm bột sữa, Gluten, cám lúc mạch, với nguồn dinh dưỡng phong phú. Tăng tính hợp khẩu vị thú cưng.</li><li>Kết hợp bền chặt: độ dẻo cao, tăng thời gian nhai, với thiết kế khoảng trống ở hai bên, có thể tiếp xúc và làm sạch răng nướu.</li><li>Bảo đảm chất lượng: công ty Welton bảo đảm với sự nghiên cứu chuyên nghiệp, sản xuất tận tâm.</li><li>Xương cho chó gặm vị sữa VEGEBRAN 360 Calcium Milk Bone với dinh dưỡng từ thịt bò chứa nhiều Protein, Axit Amin hơn thịt lợn vì thế phù hợp với yêu cầu của cơ thể</li><li>Nâng cao khả năng kháng khuẩn, có thể cung cấp lượng protein cao cho cơ thể.</li><li>Dinh dưỡng từ lúa mạch: lúa mạch giàu dinh dưỡng, Vitamin, Protein, Vitamin E, mỡ và Axit Linoleic.</li><li>Dinh dưỡng từ chất xơ: chất xơ trong răng chủ yếu gồm những chất có trong ngũ cốc, có hiệu quả thúc hoạt động đường ruột, có lợi trong việc thải phân và nước tiểu.</li><li>Tinh bột gạo: trong tinh bột gọi chứa ít hàm lượng Protein và chất béo, tăng cường hấp thụ dinh dưỡng.</li></ul><h2><strong>Thành phần dinh dưỡng</strong></h2><p>Xương cho chó gặm vị sữa VEGEBRAND 360 Calcium Milk Bone gồm các thành phần như tinh bột gạo, bột sữa, Gluten, bột mỳ, Glycerin, cám lúa mạch, đường mạch nha, Kali Sorbate… Protein 15%, chất béo 5%, chất xơ 4%, tro 4%, hàm lượng nước 16%, Canxi 0.03%, Phốt pho 0.02%.</p>',0,100,'Xương cho chó gặm vị sữa VEGEBRAND 360 Calcium Milk Bone',_binary '',0,0,38,7,0),(30,'sua-bot-cho-bbn-goats-milk-new-zealand.jpg, sua-bot-cho-cho-bbn-goats-milk-new-zealand2.jpg, sua-bot-cho-cho-bbn-goats-milk-new-zealand3.jpg',NULL,320000,320000,'<p>Sữa bột cho chó <a href=\"https://www.petmart.vn/thuong-hieu/bbn\">BBN</a> Goat’s Milk New Zealand là dòng sữa bột dành cho tất cả các giống chó mọi lứa tuổi, từ chó con mới đẻ cho đến chó mẹ đang mang thai và cho con bú.</p><h2><strong>Lợi ích chính</strong></h2><ul><li>Sản phẩm sữa bột cho chó BBN Goat’s Milk New Zealand được chế biến sản xuất từ sữa dê thô đặc biệt tại New Zealand trên dây chuyền công nghệ cao.</li><li>Đảm bảo giữ được hàm lượng dinh dưỡng cao nhất.</li><li>Sản phẩm này có thể thay thế cho sữa chó mẹ với nguồn dinh dưỡng giàu Protein, Canxi, không chứa chất gây dị ứng dành cho chó ở mọi lứa tuổi có thể sử dụng.</li><li>Sữa bột cho chó BBN Goat’s Milk New Zealand rất tốt cho dạ dày của thú cưng.</li><li>Kích thích tiêu hóa tránh táo bón.</li><li>Thành phần có chứa cả EFG có trong sữa chó mẹ giúp bảo vệ dạ dày của chó con một cách tốt nhất.</li></ul><h2><strong>Hướng dẫn cách dùng</strong></h2><p>Pha theo tỉ lệ 1 thìa gạt với 30ml nước ấm.</p><ul><li><strong>Đối với chó mèo con:</strong> 2 – 3 thìa (cho uống từ 2 – 3 lần mỗi ngày)</li><li><strong>Đối với chó mèo trưởng thành:</strong> 3 – 4 thìa (cho uống từ 1 – 2 lần mỗi ngày)</li><li><strong>Đối với chó già:</strong> 4 – 5 thìa (cho uống mỗi ngày một lần)</li></ul><p>Bảo quản trong môi trường mát mẻ, khô ráo, tránh ánh sáng trực tiếp. Không nên cho sữa vào tủ lạnh, nên sử dụng càng sớm càng tốt sau khi đã mở hộp ra. Sản phẩm này dùng được&nbsp;cho cả chó và mèo.</p>',0,98,'Sữa bột cho chó BBN Goat’s Milk New Zealand',_binary '',0,0,44,7,0),(31,'xuong-cho-cho-vi-sua-bo-vegebrand-orgo-high-calcium-oat-milk.jpg',NULL,25000,25000,'<p>Xương cho chó vị sữa bò <a href=\"https://www.petmart.vn/thuong-hieu/vegebrand\">VEGEBRAND</a> Orgo High Calcium Oat Milk là sản phẩm dinh dưỡng dành cho các giống chó có kích thước trung bình.</p><h2><strong>Lợi ích chính</strong></h2><ul><li>Sản phẩm xương gặm có thể sử dụng cho ăn trực tiếp hoặc cũng có thể làm đồ ăn thưởng cho chú chó của bạn</li><li>Xương cho chó vị sữa bò VEGEBRAND Orgo High Calcium Oat Milk được tuyển chọn từ nguyên liệu sữa thơm ngon, mỗi một chiếc đều chứa hàm lượng canxi cao</li><li>Bổ sung Canxi hiệu quả, giúp xương khỏe mạnh, răng rắn chắc</li><li>Thiết kế hình xương cũng phù hợp với tập tính thích nhai của loài chó, có tác dụng hiệu quả trong việc mát xa nướu, nâng cao khả năng nhai</li><li>Cho thêm công thức yến mạch, có thể cải thiện khả năng tiêu hóa, thúc đẩy hoạt động đường ruột, cải thiện hiệu quả vấn đề táo bón</li></ul><h2><strong>Thành phần dinh dưỡng</strong></h2><p>Protein ≥ 18%, Canxi 0.5-1.8%, Bột tro ≤ 5%, Chất xơ ≤ 5%, Hàm lượng nước ≤ 15%, Phốt Pho 0.5-1.5%</p><p>Xương cho chó vị sữa bò VEGEBRAND Orgo High Calcium Oat Milk được kiểm soát nghiêm ngặt các nguyên liệu tự nhiên từ những nguyên liệu tươi ngon, không chứa hoocmon và chất kháng sinh, trước khi chế biến, kiểm tra các tiêu chuẩn lý hóa như Protein, chất béo, tiêu chuẩn sinh vật như chất vi sinh, hợp lí mới bắt đầu tiến hành sản xuất. Sản phẩm hình xương giàu chất dinh dưỡng được chế biến từ thịt bò tươi ngon chất lượng ngon, giàu vitamin và protein, có thể tăng cường thể chất cũng như làm sạch răng.</p><ul style=\"list-style-type:disc;\"><li><p style=\"margin-left:40px;\">Nguồn sữa chất lượng tốt.</p></li><li><p style=\"margin-left:40px;\">Yến mạch giàu năng lượng.</p></li><li><p style=\"margin-left:40px;\">Giàu nguyên tố vi lượng.</p></li><li><p style=\"margin-left:40px;\">Sử dụng gelatin thực phẩm.</p></li><li><p style=\"margin-left:40px;\">Không có chất bảo quản, chất kích thích ăn, sắc tố.</p></li><li><p style=\"margin-left:40px;\">Tuyệt đối không cho chất hóa học.</p></li></ul>',0,100,'Xương cho chó vị sữa bò VEGEBRAND Orgo High Calcium Oat Milk',_binary '',0,0,38,7,0),(32,'pate-cho-cho-vi-thit-bo-tron-rau-cu-iris-chicken-beef-vegetable-400x400.jpg',NULL,35000,35000,'<p><a href=\"https://www.petmart.vn/pate-cho\">Pate cho chó</a> vị thịt bò trộn rau củ <a href=\"https://www.petmart.vn/thuong-hieu/iris-ohyama\">IRIS OHYAMA</a> Chicken Beef Vegetable là sản phẩm dành cho tất cả giống chó. Sản 